/**
 * https://leetcode.com/problems/substring-wordsIth-concatenation-of-all-words/
 *
 * You are given a string s and an array of strings words of the same length.
 * Return all starting indices of substring(s) in s that is a concatenation of each word
 * in words exactly once, in any order, and wordsIthout any intervening characters.
 * You can return the answer in any order.
 *
 */

class Solution
{
public:
    std::vector<int> findSubstring(std::string s, std::vector<std::string> &words)
    {
        std::vector<int> sequenceIndicies;
        if (words.size() == 0)
            return sequenceIndicies;

        int wordSize = words[0].size();

        // already compared, (not available) words
        std::deque<std::string> slidingWindow;
        size_t slidingWindowSize;

        // still available words
        std::unordered_map<std::string, int> availableWords;
        // counter how many words are still in availableWords map
        int availableWordsCount = words.size(); // words.size() is maximum value

        // copy words to unordered map
        std::vector<std::string>::const_iterator wIt;
        for (wIt = words.begin(); wIt != words.end(); wIt++)
            if (availableWords.find(*wIt) == availableWords.end())
                availableWords[*wIt] = 1;
            else
                availableWords[*wIt]++;

        // shift we will start our algorithm with
        for (int shift = 0; shift < wordSize; shift++)
        {
            // restore all available words and clear sliding window
            slidingWindowSize = slidingWindow.size();
            for (int i = 0; i < slidingWindowSize; i++)
            {
                availableWords[slidingWindow.front()]++;
                availableWordsCount++;
                slidingWindow.pop_front();
            }

            // start algorithm
            for (int i = shift; i < s.length(); i += wordSize)
            {
                std::string word = s.substr(i, wordSize);

                // if word doesnt exist, than delete clear sliding window
                if (availableWords.find(word) == availableWords.end())
                {
                    if (slidingWindow.size() != 0)
                    {
                        slidingWindowSize = slidingWindow.size();
                        for (int i = 0; i < slidingWindowSize; i++)
                        {
                            availableWords[slidingWindow.front()]++;
                            availableWordsCount++;
                            slidingWindow.pop_front();
                        }
                    }
                    // wait until existing word found
                    continue;
                }

                // if word is not available, start deleting words from
                // sliding window, until we'll get it
                if (availableWords[word] == 0)
                {
                    if (slidingWindow.size() != 0)
                    {
                        slidingWindowSize = slidingWindow.size();
                        for (int i = 0; i < slidingWindowSize; i++)
                        {
                            if (word != slidingWindow.front())
                            {
                                // delete word from sliding window front
                                availableWords[slidingWindow.front()]++;
                                availableWordsCount++;
                                slidingWindow.pop_front();
                            }
                            else
                            {
                                // found needed word, append it to the back
                                slidingWindow.pop_front();
                                slidingWindow.push_back(word);
                                break;
                            }
                        }
                    }
                }
                // if word is available, then add it to the sliding window and remove from available
                else
                {
                    availableWords[word]--;
                    availableWordsCount--;
                    slidingWindow.push_back(word);
                }

                // if all words were found, add index to result
                if (availableWordsCount == 0)
                {
                    sequenceIndicies.push_back(i - (slidingWindow.size() - 1) * wordSize);
                    // remove first word in the sliding window for the next cycle
                    availableWords[slidingWindow.front()]++;
                    availableWordsCount++;
                    slidingWindow.pop_front();
                }
            }
        }

        return sequenceIndicies;
    }
};

